"use strict";var progresses=document.querySelectorAll(".skills-box span.prog"),skills=document.querySelector("#Skills"),stats=document.querySelector("#Stats"),counts=document.querySelectorAll(".stat span.number");function counter(t){var e=t.dataset.goal,o=setInterval((function(){+t.innerText<e?t.innerText++:clearInterval(o)}),3e3/e)}window.onscroll=function(){window.scrollY>=skills.offsetTop&&progresses.forEach((function(t){t.style="--width: ".concat(t.dataset.width,"%;")})),window.scrollY>=stats.offsetTop&&counts.forEach((function(t){return counter(t)}))};